import { SubscriptionCardAsyncTokenRequest } from "./../../lib/types/subscription_card_async_token_request";
export interface ICardAsyncService {
    /**
     *  Token
     * @param tokenRequest - tokens request body
     * @param mid - public merchant id
     * @param isTest - is test environment
     * @param regional - Define if endpoint used regional URL
     */
    requestCardAsyncToken(tokenRequest: CardAsyncTokenRequest, mid: string, isTest: boolean, regional: boolean): Observable<CardAsyncTokenResponse>;
    /**
     *  SubscriptionAsyncToken
     * @param tokenRequest - tokens request body
     * @param mid - public merchant id
     * @param isTest - is test environment
     * @param regional - Define if endpoint used regional URL
     */
    requestCardSubscriptionAsyncToken(tokenRequest: SubscriptionCardAsyncTokenRequest, mid: string, isTest: boolean, regional: boolean): Observable<CardAsyncTokenResponse>;
}
