/* tslint:disable:all */

export type PaymentDataResponse = CardPaymentDataResponse | TransferPaymentDataResponse | CashPaymentDataResponse;

export interface CardPaymentDataResponse {
  id: string;
  email: string;
  kind: "card";
  brand: string;
  cardHolderName: string;
  expDate: string;
  lastFourDigits: string;
  maskedCardNumber: string;
}
export interface TransferPaymentDataResponse {
  id: string;
  bankId: string;
  brand: string;
  documentNumber: string;
  documentType: string;
  email: string;
  kind: "transfer";
  name: string;
  userType: string;
}
export interface CashPaymentDataResponse {
  id: string;
  brand: string;
  documentNumber: string;
  documentType: string;
  email: string;
  kind: "cash";
  name: string;
  firstName?: string;
  lastName?: string;
}
