export type PayoutsTransferTokenRequest = ColombianTokenRequest | ChileanTokenRequest | EcuadorianTokenRequest;

export interface ColombianTokenRequest {
  documentNumber: string;
  accountNumber: string;
  accountType: "CC" | "CA";
  bankId: string;
  totalAmount: number;
  documentType: "CC" | "NIT" | "CE" | "TI" | "PP";
  currency: "COP";
  paymentDescription?: string;
}
export interface ChileanTokenRequest {
  name: string;
  documentNumber: string;
  accountNumber: string;
  accountType: "CC" | "CA";
  bankId: string;
  totalAmount: number;
  documentType: "RUT" | "TI" | "PP" | "CC";
  currency: "CLP" | "UF";
  paymentDescription?: string;
}
export interface EcuadorianTokenRequest {
  documentNumber: string;
  accountNumber: string;
  accountType: "CC" | "CA";
  bankId: string;
  totalAmount: number;
  documentType: "CI" | "PAS" | "RUC";
  currency: "USD";
  paymentDescription?: string;
  name: string;
}
