export type TransferTokenRequest =
  | ColombianTokenRequest
  | ChileanTokenRequest
  | MexicanTokenRequest
  | BrazilianTokenRequest
  | CostaRicaTokenRequest
  | PeruvianTokenRequest
  | EcuadorTokenRequest;
export type CallbackUrl = string;
export type UserType = "0" | "1";
export type UserId = string | null;
export type SessionId = string | null;
export type DocumentNumber = string;
export type PaymentDescription = string;
export type Email = string;

export interface ColombianTokenRequest {
  amount: Amount;
  callbackUrl: CallbackUrl;
  userType: UserType;
  userId?: UserId;
  sessionId?: SessionId;
  documentNumber: DocumentNumber;
  paymentDescription?: PaymentDescription;
  email?: Email;
  documentType: "CC" | "NIT" | "CE" | "TI" | "PP";
  currency?: "COP";
  bankId: string;
  savePaymentData?: boolean;
  [k: string]: any;
}
export interface Amount {
  subtotalIva: number;
  subtotalIva0: number;
  iva: number;
  extraTaxes?: {
    propina?: number;
    tasaAeroportuaria?: number;
    agenciaDeViajes?: number;
    iac?: number;
    [k: string]: any;
  };
  [k: string]: any;
}
